Global site parameters On top of Hugo global configuration , Dot lets you define the following parameters in your config.toml (here, values are default).
Note that some of these parameters are explained in details in other sections of this documentation.
######################## default configuration #################### baseURL = &#34;https://demo.gethugothemes.com/dot/site/&#34; title = &#34;Dot&#34; theme = [&#34;dot-hugo&#34;] # Default time zone for time stamps; use any valid tz database name: https://en.wikipedia.org/wiki/List_of_tz_database_time_zones#List timeZone = &#34;UTC&#34; # post pagination paginate = &#34;6&#34; # see https://gohugo.io/extras/pagination/ # post excerpt summaryLength = &#34;10&#34; # see https://gohugo.io/content-management/excerpts/ # google analytics googleAnalytics = &#34;&#34; # example: UA-123-45, for more info, read the article https://support.google.com/analytics/answer/1008080?hl=en # disqus short name disqusShortname = &#34;themefisher-template&#34; # we use disqus to show comments in blog posts . To install disqus please follow this tutorial https://portfolio.peter-baumgartner.net/2017/09/10/how-to-install-disqus-on-hugo/ # disable language disableLanguages = [] # example: [&#34;fr&#34;] for disable french language. see https://gohugo.io/content-management/multilingual/ hasCJKLanguage = false # If hasCJKLanguage true, auto-detect Chinese/Japanese/Korean Languages in the content. see: https://gohugo.io/getting-started/configuration/#hascjklanguage ############################# Build ############################## [build] writeStats = true ############################# Outputs ############################## [outputs] home = [&#34;HTML&#34;, &#34;JSON&#34;, &#34;WebAppManifest&#34;] ############################# Imaging ############################## [imaging] # See https://github.com/disintegration/imaging # Default JPEG or WebP quality setting. Default is 75. quality = 90 ############################# Caches ############################### [caches] [caches.images] dir = &#34;:resourceDir/_gen&#34; maxAge = &#34;720h&#34; [caches.assets] dir = &#34;:resourceDir/_gen&#34; maxAge = &#34;720h&#34;
